function userLog(state=null, action){
    switch(action.type){

        case 'LOGIN_SUCCESS':
        return action.userData

        case 'LOGIN_OUT':    
        return {}

        default:

        return state


        case "CURRENT_USER":
        return action.userInfo


    }
}


export default userLog
